mapper.insert:

INSERT INTO "user" (
		  first_name
		, last_name
		, date_of_birth
		, email
		, password
		, contact
		, gender
		, create_by
		, create_date
		, status
		, description
		, address_id
		, resource_id
		)
VALUES (
		#{firstName}
		, #{lastName}
		, #{dateBirth}
		, #{email}
		, #{password}
		, #{contact}
		, #{gender}
		, #{userID}
		, to_char(NOW(), 'YYYYMMDD')
		, #{status}
		, COALESCE(#{description}, '' )
		, COALESCE(#{addressID}, 0)
		, COALESCE(#{resourceID}, 0)
		);

mapper.update:

UPDATE user
SET
	first_name		= #{firstName}
	, last_name		= #{lastName}
	, date_of_birth	= #{dateBirth}
	, email			= #{email}
	, password		= #{password}
	, contact		= #{contact}
	, gender		= #{gender}
	, modify_by		= #{userID}
	, modify_date	= #{firstName}
	, status		= #{status}
	, description	= COALESCE(#{description}, '' )
	, address_id	= #{addressID}
	, resource_id	= #{resourceID}
WHERE id			= #{id}
